const silentAudio = 'data:audio/mp3;base64,'

/**
 * Plays a silent HTMLAudioElement which causes iOS to unmute even if the mute toggle is on
 */
export class AudioElement {
	constructor(title){

		this.element = document.createElement('audio')
		this.element.controls = false
		this.element.preload = 'auto'
		this.element.loop = false
		this.element.src = silentAudio

		//set the title that appears on iOS lock screen
		this.element.title = title
	}

	click(){
		this.element.play()
	}
}
